        lv_obj_add_event_cb(file_explorer, file_explorer_event_cb, LV_EVENT_VALUE_CHANGED, parent);

        /* Modal Dialogue Box */
        lv_obj_add_flag(lv_layer_top(), LV_OBJ_FLAG_CLICKABLE);
